import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    addBookCart: [],
    totalQunity: 0
}

const bookCart = createSlice({
    name: "Cart",
    initialState,
    reducers: {
        addBook: (state, action) => {
            let find = state.addBookCart.findIndex((item) => item.id === action.payload.id)
            if (find >= 0) {
                state.addBookCart[find].quantity +=1
            } else {
                state.addBookCart.push(action.payload)
            }

        },
        totalBook:(state)=>{
            state.totalQunity +=1
        },
        removeBook:(state,action)=>{
            state.addBookCart = state.addBookCart.filter((item)=>item.id !== action.payload.id);
        }
    }
})
console.log(bookCart.actions)
export default bookCart;
export const { addBook, totalBook, removeBook} = bookCart.actions;
