import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    cart:0
}

export const Slice = createSlice({
    initialState,
    name: "Add item",
    reducers: {
        addCart: (state) => { state.value},
    }
})

export const { addCart } = Slice.actions;
export default Slice.reducer;
