import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    cart: []
}

export const Slice = createSlice({
    initialState,
    name: "Add item",
    reducers: {
        addCart: (state, action) => { state.cart.push(action.payload) },
    }
})

export const { addCart } = Slice.actions;
export default Slice.reducer;
