import {createSlice} from "@reduxjs/toolkit";

const initialState = {
    addBookCart:[],
    totalQunity:0
}

const bookCart = createSlice({
    name:"Cart",
    initialState,
    reducers:{
        addBook(state,action){
            state.push(action.payload)
        }
    }
})
console.log(bookCart.actions)
export default bookCart;
export const {addBook} = bookCart.actions;
